javascript:
$(function() {

  $( document ).ready(function() {

    //brand list
    var qty = 0;
    var isGetQuote = 0;
    var price = 0;
    var count = 0;

    $.ajax({
      type: 'GET',
      url: '/api/method/shopchemical.api.shoppingcart.get_brand',
      success: function(data) 
      {
        $.each(data, function () {
          $.each(this, function (i, value) {
            $("#brand-nav").append("<a id='"+value.name+"'>"+value.name+"</a>");
          });
        });
        var str = "", str1 = "";
        $.each(data, function () {
          $.each(this, function (i, value) {
            str = str + '<div class="content">'+
              '<div class="datagrid" id="'+value.name+'">'+
                '<table><thead>'+
                  '<th>Product Name</th>'+
                  '<th>Item Group</th>'+  
                  '<th>Brand</th>'+
                  '<th>MSDS</th>'+
                  '<th>Availanle Qty</th>'+
                  '<th>Price</th>'+
                '</thead><tbody></tbody></table>'+
              '</div></div>';

              str1 = str1 +  '<a href="#'+value.name.replace(/ /g,'').toLowerCase()+'" class="">'+value.name+'</a>'+
              '<div id="'+value.name.replace(/ /g,'').toLowerCase()+'">'+
               ' <table class="scrolltable">'+
                 ' <thead>'+
                     ' <tr>'+
                        '<th>Product Name</th>'+
                       /* '<th>Item Group</th>'+  
                        '<th>Brand</th>'+
                        '<th>MSDS</th>'+*/
                        '<th>Availanle Qty</th>'+
                        '<th>Price</th>'+
                      '</tr>'+
                 ' </thead>'+
                 ' <tbody>'+
                  '</tbody>'+
              '</table>'+
              '</div>';
          });
        });
        $("#brand-nav").after(str);
        $("#accordion").append(str1);
        if($(window).width() < 767)
        {
            $(".tabs").css("display","none");
            $("#accordion").css("display","block");
            setTimeout(function(){ $("#accordion div[id='"+window.location.href.split("cat=")[1].replace(/%20/g, "").toLowerCase()+"']").prev().trigger('click');}, 100);
        } else {
            $("#accordion").css("display","none");
            $(".tabs").css("display","block");
        }

        $( window ).resize(function() {
           if($(window).width() < 767)
            {
                $(".tabs").css("display","none");
                $("#accordion").css("display","block");
                setTimeout(function(){ $("#accordion div[id='"+window.location.href.split("cat=")[1].replace(/%20/g, "").toLowerCase()+"']").prev().trigger('click');}, 100);
            } else {
                $("#accordion").css("display","none");
                $(".tabs").css("display","block");
            }
        });

        $("#accordion div table.scrolltable").each(function() {
          $(this).on('scroll', function () {
              $("table > *").width($(this).width() + $(this).scrollLeft());
          });
        });
      }
    }).done(function(){
        $('.tabs nav a').on('click', function() {
          show_content($(this).index());
          if ($(".content.visible").children().children()[0].tBodies[0].rows.length == 0) {
            $.ajax({
                type: 'GET',
                url: '/api/method/shopchemical.api.shoppingcart.all_product_brand?brand='+($(this).attr("id").replace(/%20/g, " ")),
                success: function(data) 
                {
                  $.each(data, function () {
                    $.each(this, function (i, value) {

                        price = ((value.price_list_rate == null) ? 0 : value.price_list_rate);
                        qty = ((value.available_qty == null) ? 0 : value.available_qty);

                        $("div[id='"+$(".content.visible").children().attr("id")+"'] tbody").append(
                          '<tr><td>'+ '<a href='+value.item_route+'>' +value.name+'</a>'+'</td>'
                          /*+'<td>'+value.name+'</td>'*/
                          +'<td>'+value.item_group+'</td>'
                          +'<td>'+((value.brand == null) ? '-' : value.brand)+'</td>'
                          +'<td>'+((value.msds == null) ? '-' : value.msds)+'</td>'
                          +'<td style="text-align:right">'+qty+'</td>'
                          +'<td style="text-align:right">'+(((value.show_get_quote == 0 && qty == 0) || value.show_get_quote == 1) ? '<span><button class="btn btn-warning btn-sm getQuote center-block" data-get-itemcode="'+value.name+'">Get Quote</button></span>' : price)+'</td></tr>'
                        );
                    });
                });
              }
            });
          }
        });


        $("#accordion a").on('click', function() {
          console.log($(this));
          var me = $(this);
            $.ajax({
                type: 'GET',
                url: '/api/method/shopchemical.api.shoppingcart.all_product_brand?brand='+($(this).next().attr("id").replace(/%20/g, " ")),
                success: function(data) 
                {
                  $.each(data, function () {
                  $.each(this, function (i, value) {
                    var image="";
                    if(value.thumbnail != "") {
                      image = value.thumbnail;
                    }
                    else
                    { image = value.website_image; }

                    if (image == "" || image == null) {
                      image = "/files/no-image-box.png";
                    }

                    price = ((value.price_list_rate == null) ? 0 : value.price_list_rate);
                    qty = ((value.available_qty == null) ? 0 : value.available_qty);

                    //if (value.item_group.replace(/ /g,'').toLowerCase() == me.next().attr("id")) {
                        $("#accordion div[id='"+me.next().attr("id").replace(/ /g,'').toLowerCase()+"'] table tbody").append(
                          '<tr><td>'+ '<a href='+value.item_route+'>' +value.name+'</a>'+'</td>'
                          //+'<td>'+value.name+'</td>'
                         /* +'<td>'+value.item_group+'</td>'
                          +'<td>'+((value.brand == null) ? '-' : value.brand)+'</td>'
                          +'<td>'+((value.msds == null) ? '-' : value.msds)+'</td>'*/
                          +'<td>'+((qty == 0) ? '-' : qty)+'</td>'
                          +'<td>'+(((value.show_get_quote == 0 && qty == 0) || value.show_get_quote == 1 || price == 0) ? '<span><button class="btn btn-warning btn-sm getQuote center-block" data-get-itemcode="'+value.name+'">Get Quote</button></span>' : price)+'</td></tr>'
                        );
                      //}
                  });
                });
              }
            });

        });
        // display the first div by default.
        $("#accordion div").first().css('display', 'block');


        // Get all the links.
        var link = $("#accordion a");

        // On clicking of the links do something.
        link.on('click', function(e) {

            e.preventDefault();

            var a = $(this).attr("href");

            $(a).slideDown('fast');

            //$(a).slideToggle('fast');
            $("#accordion div").not(a).slideUp('fast');

        });

        show_content(0);

        function show_content(index) {
          // Make the content visible
          $('.tabs .content.visible').removeClass('visible');
          $('.tabs .content:nth-of-type(' + (index + 1) + ')').addClass('visible');

          // Set the tab to selected
          $('.tabs nav a.selected').removeClass('selected');
          $('.tabs nav a:nth-of-type(' + (index + 1) + ')').addClass('selected');
        }
  });
  //trigger click to 1st tab
  setTimeout(function(){ 
    $(".tabs nav a[id='"+window.location.href.split("cat=")[1].replace(/%20/g, " ")+"']").trigger('click');
  }, 500);

});
});

css:
h1, p {
    margin: 0;
    padding: 0 0 .5em;
}

.font-color{color:#7ece32;font-weight:bolder;}
.container-main {
    /*margin: 0 auto;*/
    max-width: 480px;
}

/*
 * Caption component
 */
.caption {
    position: relative;
    overflow: hidden;

    /* Only the -webkit- prefix is required these days */
    -webkit-transform: translateZ(0);
            transform: translateZ(0);
    margin: 8px;
}

.caption::before {
    content: ' ';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: transparent;
    transition: background .35s ease-out;
}

.caption:hover::before {
    background: rgba(0, 0, 0, .5);
}

.caption__media {
    display: block;
    min-width: 100%;
    max-width: 100%;
    height: 30vh;
}

.caption__overlay {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    padding: 10px;
    color: white;

    -webkit-transform: translateY(100%);
            transform: translateY(100%);

    transition: -webkit-transform .35s ease-out;
    transition:         transform .35s ease-out;
}

.caption:hover .caption__overlay {
    -webkit-transform: translateY(0);
            transform: translateY(0);
}

.caption__overlay__title {
    -webkit-transform: translateY( -webkit-calc(-100% - 10px) );
            transform: translateY( calc(-100% - 10px) );

    transition: -webkit-transform .35s ease-out;
    transition:         transform .35s ease-out;
}

.caption:hover .caption__overlay__title {
    -webkit-transform: translateY(0);
            transform: translateY(0);
}

.text-center {display:none;}

