	                    image = "/files/no-image-box.png";
	                }
	                else
	                {
	                    image = value.category_image;
	                }

	                $('#category-list').append('<div class="col-md-4 col-sm-6 col-xs-6">'+
	                '<div class="container-main">'+
	                	'<a class="thumbnail" href="/allcategories?cat='+value.name+'" rel="nofollow" style="box-shadow: 0px 1px 5px 0px #404040;">'+
					    '<article class="caption">'+
					        '<img class="caption__media" src="'+image+'" />'+
					        '<div class="caption__overlay">'+
					            '<h1 class="caption__overlay__title font-color">'+value.name+'</h1>'+
					            /*'<p class="caption__overlay__content">'+((value.description == null) ? 'No Data Available' : value.description)+' </p>'+*/
					        '</div>'+
					    '</article>'+
					    '</a>'+
