#accordion {
     margin-top:10px;
     border:thin solid #cecece;   
     border-top:none;
     border-bottom:none;
}

#accordion div {
    background:white;
   /* height:100px;*/
    /*line-height:100px;*/
    display:none;
    border-bottom:thin solid #cecece;
    padding-left:15px;
}

#accordion a {
    display:block;
   /* width:483px;*/
    background:#f4f4f4;
    background-image: -webkit-linear-gradient(white,#ededed);
    background-image: -moz-linear-gradient(white,#ededed);
    background-image: -o-linear-gradient(white,#ededed);
    background-image: -ms-linear-gradient(white,#ededed);
    background-image:linear-gradient(white,#ededed);
    color:#959696;
    padding-left:15px;
    height:40px;
    line-height:40px;
    text-decoration:none;
    border-bottom:thin solid #cecece;
    font-family:Arial;
    font-size:13px;
    font-weight:bold;
    text-shadow:0px 1px 1px white;
}

.first {
    border-top:thin solid #cecece;    
}

.scrolltable {
    border-collapse: collapse;
    width: 300px;
    overflow-x: scroll;
    display: block;
}
/*.scrolltable thead {
    background-color: #EFEFEF;
}*/
.scrolltable thead, .scrolltable tbody {
    display: block;
}
.scrolltable tbody {
    overflow-y: scroll;
    overflow-x: hidden;
    height: 140px;
}
/*.scrolltable td, .scrolltable th {
    min-width: 100px;
    height: 25px;
    border: dashed 1px lightblue;
}*/
::-webkit-scrollbar {
    -webkit-appearance: none;
}
::-webkit-scrollbar:vertical {
    width: 12px;
}
::-webkit-scrollbar:horizontal {
    height: 12px;
}
::-webkit-scrollbar-thumb {
    background-color: rgba(0, 0, 0, .5);
    border-radius: 10px;
    border: 2px solid #ffffff;
}
::-webkit-scrollbar-track {
    border-radius: 10px;
    background-color: #ffffff;
}

