javascript:
$(function() {
	var qty = 0;
	var isGetQuote = 0;
	var price = 0;
	var count = 0;
$.ajax({
    type: 'GET',
    url: "/api/method/shopchemical.api.shoppingcart.all_product",
    success: function(data) 
    {
      	$.each(data, function () {
			$.each(this, function (i, value) {
				var image="";
				if(value.thumbnail != "") {
					image = value.thumbnail;
				}
				else
				{ image = value.website_image; }

				if (image == "" || image == null) {
					image = "/files/no-image-box.png";
				}

				if (value.available_qty == null || value.available_qty == "" || value.available_qty == "null") 
				{
					qty = 0;
				}
				else
				{
					qty = value.available_qty;
				}

				if (value.price_list_rate == null || value.price_list_rate == "" || value.price_list_rate == "null") 
				{
					price = 0;
				}
				else
				{
					price = value.price_list_rate;
				}

				//console.log(image.length);
				$(".prod-item").append('<div class="col-sm-4 col-md-3 col-xs-12 prod">'+
					'<div class="thumbnail" style="box-shadow: 0px 1px 5px 0px #404040;">'+
						'<h4>'+
							'<a href="/'+value.group_route+'">'
						        +value.item_group+
						    '</a>'+	
						'</h4>'+
						'<div class="thumbnail">'+
							'<img alt="Generic placeholder thumbnail" src="'+image+'">'+
						'</div>'+
						'<div class="caption">'+
							'<h4>'+
								'<a href="/'+value.item_route+'">'
								+value.name+
								'</a>'+
							'</h4><br>'+
							'<span><h4>Price: '+price+'</h4>'+
							'<h4>Available Qty: '+qty+'</h4></span>'+
						'</div>'+
						'<hr>'+
						'<div class="row" id="cart-detail_'+count+'">'+
							'<div class="col-sm-12 goToCart" style="display:none;">'+
								'<div id="item-update-cart">'+
									'<a href="/cart">Goto Cart</a>'+
								'</div>'+
							'</div>'+
							'<div class="col-sm-6 col-xs-6 addToCart">'+
								'<div class="input-group number-spinner">'+
					               ' <span class="input-group-btn">'+
					                    '<button class="btn btn-default cart-btn" data-dir="dwn">'+
					                        '–</button>'+
					                '</span>'+
						            '<input class="form-control text-right cart-qty" value="0" data-item-code="'+value.item_code+'">'+
						                '<span class="input-group-btn">'+
						                    '<button class="btn btn-default cart-btn" data-dir="up" style="margin-left:-2px;">'+
						                        '+</button>'+
						                '</span>'+
						            '</input>'+
								'</div>'+
							'</div>'+
							'<div class="col-sm-6 col-xs-6 addToCart">'+
								'<div class="item-cart">'+
								    '<div id="item-add-to-cart">'+
								        '<button class="btn btn-primary btn-sm" style="display: block;" data-get-itemcode="'+value.item_code+'">'+
								            'Add to Cart</button>'+
								    '</div>'+
								'</div>'+
							'</div>'+
						'</div>'+
					'</div>'+
				'</div>');


				if ( value.show_get_quote == 1 ) {
					$('<span><button class="btn btn-warning btn-sm getQuote center-block" data-get-itemcode="'+value.name+'">Get Quote</button></span>').insertAfter("#cart-detail_"+count);
					//console.log($("#cart-detail_"+count).prev().prev().children()[2]);
					$($("#cart-detail_"+count).prev().prev().children()[2]).css("display","none");
					$("#cart-detail_"+count).css("display","none");

					//$(".caption span").css("display","none")
				}
				else if (value.show_get_quote == 0 && qty == 0) {
					$('<spn><button class="btn btn-warning btn-sm getQuote center-block" data-get-itemcode="'+value.name+'">Get Quote</button></span>').insertAfter("#cart-detail_"+count);
					//console.log($("#cart-detail_"+count).prev().prev().children()[2]);
					$($("#cart-detail_"+count).prev().prev().children()[2]).css("display","none");
					$("#cart-detail_"+count).css("display","none");

					//$(".caption span").css("display","none")
				}

				count ++;

				$(".getQuote" ).click(function() {
				  //var path = window.location.pathname.split("/");
				  var item_name = $(this).attr("data-get-itemcode");
				  //console.log(item_name);
				  window.location.href="/buying/get-quote?item_name="+item_name;
				  return false;
				});

			});
		});
    }


}); 

