$($("#page-Products").next()).css("display","none");

$(function() {

	if ($(window).width() <= 767) {
      	   $(".product-image-wrapper").css("width","50vw");
	}
	else
	{
		$(".product-image-wrapper").css("width","14vw")
	}

    if(window.location.pathname == "/" || window.location.pathname == "/index" || window.location.pathname == "/brand" ||  window.location.pathname == "/allProducts") {
       console.log(1234);
       $(".page-content").css("padding-top","0");
       $(".page-head").css("display","none");
    }        

    var itemCode = $('.text-muted span').attr('itemprop', 'productID').html();
    var qty = 0;
    var isGetQuote = 0;
  	var msdslink = "";

  	//desc position change
  	var str = $('.item-website-description p').attr('itemprop', 'description').html()
