javascript:
//$('.text-muted span').attr('itemprop', 'productID').html();

$($("#page-Products").next()).css("display","none");

$(function() {

	if ($(window).width() <= 767) {
      	   $(".product-image-wrapper").css("width","50vw");
	}
	else
	{
		$(".product-image-wrapper").css("width","14vw")
	}

    if(window.location.pathname == "/" || window.location.pathname == "/index" || window.location.pathname == "/brand" ||  window.location.pathname == "/allProducts") {
       console.log(1234);
       $(".page-content").css("padding-top","0");
       $(".page-head").css("display","none");
    }        

    var itemCode = $('.text-muted span').attr('itemprop', 'productID').html();
    var qty = 0;
    var isGetQuote = 0;
  	var msdslink = "";

  	//desc position change
  	var str = $('.item-website-description p').attr('itemprop', 'description').html()
  	$(str).insertBefore( ".item-attribute-selectors" );

    $.ajax({
		type: 'GET',
		url: encodeURI("/api/method/shopchemical.api.shoppingcart.get_available_qty?item_code="+itemCode),
		async: false,
		success: function(data) 
		{
			$.each(data, function () {
				$.each(this, function (i, value) {
					if (value.available_qty == null || value.available_qty == "") {
						qty = 0;
					}
					else
					{
						console.log(value.available_qty);
						qty = value.available_qty;
					}
					$("<h4 id='aQty'>Available QTY: "+qty+"</h4>").insertAfter($("#item-add-to-cart"));
				});
			});
		}
	});

    $.ajax({
	    type: 'GET',
	    url: encodeURI("/api/method/shopchemical.api.shoppingcart.get_item_detail?item_code="+itemCode),
	    async: false,
	    success: function(data) 
	    {
	    	$.each(data, function () {
				$.each(this, function (i, value) {
					isGetQuote = value.show_get_quote;
					msdslink = value.msds;
				});
			});
	    }
	});

	//getQuote
	if ( isGetQuote == 1 ) {
		$('<span><button class="btn btn-warning btn-sm getQuote">Get Quote</button></span>').insertAfter( "#item-add-to-cart" );
	}
	else if (isGetQuote == 0 && qty == 0) {
		$('<span><button class="btn btn-warning btn-sm getQuote">Get Quote</button></span>').insertAfter( "#item-add-to-cart" );
	}

	$(".getQuote" ).click(function() {
	  var path = window.location.pathname.split("/");
	  window.location.href="/buying/get-quote?item_name="+path[3];
	  return false;
	}); 

	if ($(".getQuote").length == 1) {
		//if getQuote is visible hide add to cart and Qty available
		$("#item-add-to-cart :button").css("display","none");
		$("#aQty").css("display","none");
		$(".item-price").css("display","none");
	}
	else {
		$("#item-add-to-cart :button").css("display","block");
		$("#aQty").css("display","block");
		$(".item-price").css("display","block");
	}

	if (msdslink != "" && msdslink != null) {
		$("<h2 class='item-msds'><a href='"+msdslink+"' rel='nofollow'>MSDS Link</a></h2>").insertAfter($(".item-cart"));
		$(".item-msds").css({"margin-left":"0", "margin-top":"10px"});
	}

});
